mapper.update:
update notice 
set title=#{title}, content=#{content}, filename=#{filename, jdbcType=VARCHAR}, filepath=#{filepath, jdbcType=VARCHAR} 
where id=#{id}

update notice 
set readcnt = readcnt+1 
where id=#{id}

mapper.select:
select notice.*, (select member_name from memberjoin where writer=memberjoin.member_id) name 
from notice 
where id=#{id}

mapper.insert:
{
call declare
begin

	update notice set step=step+1 where root=#{root} and step > #{step};
	insert into notice(id, title, content, writer, filename, filepath, root, step, indent)
	values (seq_id.nextval, #{title}, #{content}, #{writer}, #{filename, jdbcType=VARCHAR}, #{filepath, jdbcType=VARCHAR}
	,#{root},#{step}+1,#{indent}+1);

end
}
